import React from 'react';
import { Link } from 'react-router-dom';
import AppBar from '@material-ui/core/AppBar';
import CssBaseline from '@material-ui/core/CssBaseline';
import Grid from '@material-ui/core/Grid';
import TreeView from '@material-ui/lab/TreeView';
import ExpandMoreIcon from '@material-ui/icons/ExpandMore';
import ChevronRightIcon from '@material-ui/icons/ChevronRight';
import TreeItem from '@material-ui/lab/TreeItem';
import Drawer from '@material-ui/core/Drawer';
import Hidden from '@material-ui/core/Hidden';
import IconButton from '@material-ui/core/IconButton';
import MenuIcon from '@material-ui/icons/Menu';
import Toolbar from '@material-ui/core/Toolbar';
import Typography from '@material-ui/core/Typography';
import ResponsiveContent from './content'
import { makeStyles, useTheme } from '@material-ui/core/styles';

const drawerWidth = 240;

const useStyles = makeStyles((theme) => ({
  root: {
    display: 'flex',

  },
  drawer: {
    [theme.breakpoints.up('sm')]: {
      width: drawerWidth,
      flexShrink: 0,
    },
  },
  appBar: {
    [theme.breakpoints.up('sm')]: {
      width: `calc(100% - ${drawerWidth}px)`,
      marginLeft: drawerWidth,
    },
  },
  menuButton: {
    marginRight: theme.spacing(2),
    [theme.breakpoints.up('sm')]: {
      display: 'none',
    },
  },
  toolbar: theme.mixins.toolbar,
  drawerPaper: {
    width: drawerWidth,
  },
  content: {
    flexGrow: 1,
    padding: theme.spacing(3),
  },
}));

const data = [
  {
    id: "0001",
    title: "Getting Started",
    refTitle: 'article1',
    text: "Lorem ipsum dolor sit amet Lorem ipsum dolor sit ametLorem ipsum dolor sit ametLorem ipsum dolor sit ametLorem ipsum dolor sit amet",
    parentId: '0'
  },
  {
    id: "0002",
    title: "Creating Custom Components",
    refTitle: 'article2',
    text: "some text2",
    parentId: '0'
  },
  {
    id: "0003",
    title: "subtitle 1",
    refTitle: 'article3',
    text: "some text2",
    parentId: '0001'
  },
  {
    id: "0004",
    title: "subtitle 2",
    refTitle: 'article4',
    text: "some text3",
    parentId: '0002'
  },
  {
    id: "0005",
    title: "subtitle 3",
    refTitle: 'article5',
    text: "some text3",
    parentId: '0002'
  },
]



function ResponsiveDrawer(props) {

  const { window, to } = props;
  const classes = useStyles();
  const theme = useTheme();
  const [mobileOpen, setMobileOpen] = React.useState(false);

  const CustomLink = React.useMemo(
    () =>
      React.forwardRef((linkProps, ref) => (
        <Link ref={ref} to={to} {...linkProps} />
      )),
    [to],
  );

  const titles = data.map((item, index)=>{
    return item.parentId == 0 &&
      <TreeItem key={index} 
                nodeId={item.id} 
                label={item.title} 
                component={CustomLink}>
          {
            data.map((subItem, subIndex)=>{
              return subItem.parentId === item.id &&
                <TreeItem key={subIndex} 
                          nodeId={subItem.id} 
                          label={subItem.title} 
                          component={CustomLink}/>           
            })
          }
      </TreeItem>
})

  const handleDrawerToggle = () => {
    setMobileOpen(!mobileOpen);
  };

  const drawer = (
    <div>
      <div className={classes.toolbar} />
      <TreeView
        defaultCollapseIcon={<ExpandMoreIcon />}
        defaultExpandIcon={<ChevronRightIcon />}
      >
        {titles}
      </TreeView>
    </div>
  );

  const container = window !== undefined ? () => window().document.body : undefined;

  return (
    <div className={classes.root}>
      <CssBaseline />
      <AppBar position="fixed" className={classes.appBar}>
        <Toolbar>
          <IconButton
            color="inherit"
            aria-label="open drawer"
            edge="start"
            onClick={handleDrawerToggle}
            className={classes.menuButton}
          >
            <MenuIcon />
          </IconButton>

          <Typography variant="h6"  noWrapclassName={classes.title}>
            UltraPartners Wiki
          </Typography>

        </Toolbar>
      </AppBar>
      <nav className={classes.drawer} aria-label="mailbox folders">
        <Hidden smUp implementation="css">
          <Drawer
            container={container}
            variant="temporary"
            anchor={theme.direction === 'rtl' ? 'right' : 'left'}
            open={mobileOpen}
            onClose={handleDrawerToggle}
            classes={{
              paper: classes.drawerPaper,
            }}
            ModalProps={{
              keepMounted: true, 
            }}
          >
            {drawer}
          </Drawer>
        </Hidden>
        <Hidden xsDown implementation="css">
          <Drawer
            classes={{
              paper: classes.drawerPaper,
            }}
            variant="permanent"
            open
          >
            {drawer}
          </Drawer>
        </Hidden>
      </nav>
      <Grid container>
        <Grid item md={8}>
          <main className={classes.content}>
            <div className={classes.toolbar} />
              {data.map((text, index) => (
                <ResponsiveContent 
                  key={index} 
                  content={text} ref={refTitle}/>
              ))}
          </main>
        </Grid>
      </Grid>
    </div>

  );
}


export default ResponsiveDrawer;
