import React from 'react';
import Typography from '@material-ui/core/Typography';
import { gql, useLazyQuery } from '@apollo/client';

const GETITEM = gql`
	query GetItem($id: ID!) {
		getItem(id: $id){
			id
			title
			text
			sort
			parentId
		}
	}
`;

export default (props) => {
	console.log(props)

	const [ loadGreeting, {loading, error, data}  ] = useLazyQuery(GETITEM, {
		variables: {id: props.id ? props.id : props.match.params.id}
	});

	if (called && loading) return <p>Loading ...</p>
	if (error) return `Error! ${error}`;


	const article = data ? data.getItem : null;

	return article ? (
        <>
        <Typography variant="h4" component="h3" gutterBottom>
            {article.title}
        </Typography>
        <Typography paragraph gutterBottom>
            {article.text}
        </Typography>
        </>
	) : (
		<div>Loading...</div>
	);
};
